import Progress from "./Progress";
import React from 'react';
import { Navbar, Nav, Container, NavDropdown, Button, Row, Col, NavItem, NavLink } from 'react-bootstrap';
import Paper from '@mui/material/Paper';
import '../index.css';



const Shipment = () => {
    const formatDate = (dateString) => {
        const options = { year: "numeric", month: "long", day: "numeric" }
        return new Date(dateString).toLocaleDateString(undefined, options)
    }
    
    var timee = new Date(localStorage.time);
    var timeee = new Date(localStorage.arrivaltime);
    var numb = localStorage.trackingo;
    var progcol = '';
    var msg = '';
    if (localStorage.status == "DELIVERED_TO_SENDER"){
        progcol = "yellow"
        msg = "Not delivered yet"
    }
    else if (localStorage.status == "DELIVERED"){
        progcol = "green"
        msg = "Delivered"

    }
    else {
        progcol = "red"
        msg = "Cancelled Shipment"
    }
    return (
        <Paper className="pap">
            <Container className="shadcont">
                <Row className="rowspacing">
                    <Col className='col-6 col-md-3 '>
                        <p>Shipment Number {numb}</p>
                        <p style = {{ color: progcol }}>{msg} </p>
                    </Col>
                    <Col className='col-6 col-md-3 '>
                        <p>Latest Update</p>
                        <p>{timee.toGMTString()}</p>
                    </Col>
                    <Col className='col-6 col-md-3 '>
                        <p>Supplier </p>
                        <p>Souq.com</p>
                    </Col>
                    <Col className='col-6 col-md-3 '>
                        <p>Date of arrival</p>
                        <p>{timeee}</p>
                    </Col>
                </Row>
                <Row className="rowspacing labels">
                    <Col className='col-12 col-md-12 progg'>
                        <Progress  />
                    </Col>
                    <Col className='col-3 col-md-3 ship1'>
                        <p>Shipment Created</p>
                    </Col>
                    <Col className='col-3 col-md-3 ship2'>
                        <p className="shippad2">Shipment Received</p>
                    </Col>
                    <Col className='col-3 col-md-3 ship3'>
                        <p className="shippad3">Shipment out for delivery</p>
                    </Col>
                    <Col className='col-3 col-md-3 ship4'>
                        <p className="shippad4">Shipment Delivered</p>
                    </Col>

                </Row>
            </Container>
        </Paper>
    )
}

export default Shipment
