import React from "react";
import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from "react-step-progress-bar";
import { FaShippingFast } from 'react-icons/fa';
import { MdOutlinePresentToAll } from 'react-icons/md';
import { useState } from 'react';


import '../index.css';



const Progress = () => {
    const status = localStorage.status;
    const progcol = "";
    if (localStorage.status == "DELIVERED_TO_SENDER"){
        progcol = ""
    }
    const [col, setCol] = useState('');
    const [col1, setCol1] = useState('');
    const [col2, setCol2] = useState('');
    const [col3, setCol3] = useState('');
    const [coli, setColi] = useState('');
    const [coli1, setColi1] = useState('');
    const [coli2, setColi2] = useState('');
    const [coli3, setColi3] = useState('');
    return (
        <ProgressBar
            filledBackground="red"
            percent={67}
        >
            <Step transition="scale">
                {({ accomplished, index }) => (
                    <div
                        className={`transitionStep ${accomplished ? "accomplished" : null}`}
                    >

                        {accomplished ? setCol("red") : null}
                        {accomplished ? setColi("white") : null}
                        <div style={{ backgroundColor: col, borderColor: col }} className="icon-wrapper">
                            <FaShippingFast style={{ color: coli }} className="icon-color" />
                        </div>
                    </div>
                )}
            </Step>
            <Step transition="scale">
                {({ accomplished, index }) => (
                    <div
                        className={`transitionStep ${accomplished ? "accomplished" : null}`}

                    >
                        {accomplished ? setCol1("red") : null}
                        {accomplished ? setColi1("white") : null}
                        <div style={{ backgroundColor: col1 , borderColor: col1 }} className="icon-wrapper">
                            <FaShippingFast style={{ color: coli1 }} className="icon-color" />
                        </div>
                    </div>
                )}
            </Step>
            <Step transition="scale">
                {({ accomplished, index }) => (
                    <div
                        className={`transitionStep ${accomplished ? "accomplished" : null}`}
                    >
                    
                        {accomplished ? setCol2("red") : null}
                        {accomplished ? setColi2("white") : null}
                        <div style={{ backgroundColor: col2, borderColor: col2 }} className="icon-wrapper">
                            <FaShippingFast style={{ color: coli2 }}className="icon-color" />
                        </div>

                        

                    </div>
                )}
            </Step>
            <Step transition="scale">
                {({ accomplished, index }) => (
                    <div
                        className={`transitionStep ${accomplished ? "accomplished" : null}`}
                    >
                        {accomplished ? setCol3("red") : null}
                        {accomplished ? setColi3("white") : null}
                        <div style={{ backgroundColor: col3 , borderColor: col3 }} className="icon-wrapper-f">
                            <MdOutlinePresentToAll style={{ color: coli3 }} className="icon-color3" />
                        </div>
                    </div>
                )}
            </Step>

        </ProgressBar>
    )
}

export default Progress
